import { useEffect, useState } from "react"
import { getEstudiantes } from "./peticiones/getEstudiantes";
import { postEstudiante } from "./peticiones/postEstudiante";
import { editarEstudiante } from "./peticiones/editarEstudiante";
import { TablaLibros } from "./componentes/TablaLibros";
import { FormularioLibro } from "./componentes/FormularioLibro";

export const LibrosApp = () => {

    const [libro, setLibro] = useState([]);
    console.log(libro);

    const agregarLibro= (libro) => {
        setLibro([...libro, libro])
        postEstudiante(estudiante);
    }
    const cargueEstudiantes = async () => {
        const datos = await getEstudiantes()
        setEstudiantes(datos);
    }
    useEffect(()=>{
        cargueLibros();
    },[])
    const editarEstudiante = (libro) => {
        console.log(libro);
        setLibro(libro);
    }
    
    return (
        <>
            <FormularioLibro agregar={(lib) => { agregarLibro(lib) }} />
            <TablaLibros listaLibros={libro} />
        </>
    )
}
